class FiltroCartel extends Component{

    constructor(props){
        super(props)
        this.state = {
            valorInput : '',
        }
    }

    manejarSubmit(evento){
        evento.preventDefault();
    }

    controlarInput(evento){
        this.setState(
            {valorInput: evento.target.value},
            () => this.props.filtro(this.state.valorInput)
        )
    }

